import React, { Component } from 'react';
import List from './List';
import Dropdown from 'react-bootstrap/Dropdown';
import 'bootstrap/dist/css/bootstrap.min.css';

class FilteredList extends Component {
    constructor(props) {
        super(props);
        this.state = { filter: 'All', search: '' };
    }

    updateFilter = (type) => {
        this.setState({ filter: type });
    };

    updateSearch = (event) => {
        this.setState({ search: event.target.value });
    };

    filterItems = () => {
        return this.props.items.filter(
            (item) =>
                (this.state.filter === 'All' || item.type === this.state.filter) &&
                item.name.toLowerCase().includes(this.state.search.toLowerCase())
        );
    };

    render() {
        return (
            <div>
                <input
                    type="text"
                    placeholder="Search"
                    onChange={this.updateSearch}
                />
                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Filter
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        <Dropdown.Item onClick={() => this.updateFilter('All')}>All</Dropdown.Item>
                        <Dropdown.Item onClick={() => this.updateFilter('Fruit')}>Fruit</Dropdown.Item>
                        <Dropdown.Item onClick={() => this.updateFilter('Vegetable')}>Vegetable</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
                <List items={this.filterItems()} />
            </div>
        );
    }
}

export default FilteredList;
